import React from 'react';

const PostCardContent = ({ postData }) => {
  <div>
    {postData.split(/(#[^\s#]+)/g).map((v) => {
      if (v.match(/(#[^\s#]+)/)) {
        return (
          <Link herf={`/hashtag/${v.slice(1)}`}>
            <a>{v}</a>
          </Link>
        );
      }
      return v;
    })}
  </div>;
};

PostCardContent.propTypes = { postData: PropTypes.string.isRequired };

export default PostCardContent;
